            i = Math.floor( hue );
            f = hue - i;			/* factorial part of h */
            p = value * ( 1.0 - saturation );
            q = value * ( 1.0 - saturation * f );
            t = value * ( 1.0 - saturation * ( 1.0 - f ) );
