  /**
   * Reports the measurement of an angle between two lines
   */
  GSP.gConstraints.AngleMarker_Reflex = GSP.makeClass(GSP.gConstraints.AngleMarker, {
      "doc": {
          "description": "A reflex angle marker",
          "inherits": "AngleMarker",
          "refspec": {
              "gspRef": "AngleMarker_Reflex_C",
              "msg": "This constraint is intended to behave in the same way as the corresponding constraint, AngleMarker_Reflex_C, in the currently published version of desktop Geometer's Sketchpad."
          },
          "usage": {
              "abstract": false
          }
      },
      constrainedAngle: function(angle, orientationInfo) {
        if(orientationInfo.isStraight) {
          angle = orientationInfo.isStraight === 'fullCircle'
              ? GSP.math.TWO_PI : Math.PI;
        } else {
          if(angle <= GSP.math.SIMPLE_PI_THRESHOLD) {
            angle = -(2*Math.PI - angle);
          }
        }
        
        orientationInfo.orientation = (angle <= 0) ? -1 : 1; 
   
        return angle;
       }
  });
  
  
